// src/components/MessageDetails.jsx
import React from 'react';
import { Modal, Tabs, Typography, Image, Space, Tag } from 'antd';
import { CheckCircleTwoTone, CloseCircleTwoTone, FileImageOutlined } from '@ant-design/icons';
import dayjs from 'dayjs';

const { Text, Paragraph } = Typography;
const { TabPane } = Tabs;

const MessageDetails = ({ visible, message, onClose, isMobile }) => {
  if (!message) return null;
  
  const renderTimestamp = (timestamp) => {
    if (!timestamp) return 'N/A';
    return dayjs(timestamp).format('YYYY-MM-DD HH:mm:ss');
  };
  
  return (
    <Modal
      title={message.Title || 'Message Details'}
      open={visible}
      onCancel={onClose}
      footer={null}
      width={isMobile ? '100%' : 720}
      style={{ top: isMobile ? 0 : 20 }}
      bodyStyle={{ 
        maxHeight: '70vh', 
        overflowY: 'auto', 
        padding: isMobile ? '12px' : '24px' 
      }}
    >
      <Tabs defaultActiveKey="content" size={isMobile ? 'small' : 'middle'}>
        <TabPane tab="Content" key="content">
          <Paragraph 
            style={{ 
              whiteSpace: 'pre-wrap',
              fontSize: '16px',
              lineHeight: '1.6',
              marginBottom: '24px'
            }}
          >
            {message.Content || 'No content available'}
          </Paragraph>
        </TabPane>
        
        {message.ImagePath && (
          <TabPane tab="Image" key="image">
            <div style={{ textAlign: 'center', padding: '16px 0' }}>
              {message.ImagePath.startsWith('http') ? (
                <Image
                  src={message.ImagePath}
                  alt={message.Title}
                  style={{ maxWidth: '100%', maxHeight: '400px' }}
                  fallback="data:image/png;base64,"
                />
              ) : (
                <div style={{ 
                  display: 'flex', 
                  alignItems: 'center', 
                  justifyContent: 'center',
                  height: '200px', 
                  background: '#f5f5f5', 
                  borderRadius: '4px' 
                }}>
                  <FileImageOutlined style={{ fontSize: 64, color: '#d9d9d9' }} />
                  <Text type="secondary" style={{ marginLeft: 8 }}>
                    {message.ImagePath}
                  </Text>
                </div>
              )}
            </div>
          </TabPane>
        )}
        
        <TabPane tab="Details" key="details">
          <Space direction="vertical" style={{ width: '100%' }}>
            <div>
              <Text strong>Created Time:</Text>{' '}
              {renderTimestamp(message.Created)}
            </div>
            
            <div>
              <Text strong>Updated Time:</Text>{' '}
              {renderTimestamp(message.Updated)}
            </div>
            
            <div>
              <Text strong>Status:</Text>{' '}
              {message.Enabled ? (
                <span>
                  <CheckCircleTwoTone twoToneColor="#52c41a" /> Enabled
                </span>
              ) : (
                <span>
                  <CloseCircleTwoTone twoToneColor="#f5222d" /> Disabled
                </span>
              )}
            </div>
            
            {message.ImagePath && (
              <div>
                <Text strong>Image Path:</Text>{' '}
                <Text code copyable>{message.ImagePath}</Text>
              </div>
            )}
          </Space>
        </TabPane>
      </Tabs>
    </Modal>
  );
};

export default MessageDetails;